rules.click:
data.errorsDockLock = true;
data.dockErrors = !($(".av-menu-errors:visible").length > 0);
Form.$Pages.move(true);
$timeout(function() {	
  data.blockNext = Form.validation && Form.validation.errors.length > 0;
}, 200);
$timeout(function() {  
  data.blockNext = false;
}, 800);
$timeout(function() {  
  data.errorsDockLock = false;
}, 2000);

