rules.ok:
if(Util.isBlank(data.date) || (!item.properties.minAge && !item.properties.maxAge && !item.properties.pastDate)) {
  return true;
}
var currentTime = new Date().getTime(),
    minAge = item.properties.minAge,
    maxAge = item.properties.maxAge,
    pastDate = item.properties.pastDate,
    dobDate = Calc.date(data.date);

if(pastDate && Calc.daysBetween(dobDate, new Date()) < 0) {
    return "Must be a date in the past";
}

if(!Util.isBlank(minAge)) {
	dobDate.setFullYear(dobDate.getFullYear()+minAge);
	if (currentTime <= dobDate.getTime()) {
		return "You must be at least "+minAge+" years old";
	}
}

if(!Util.isBlank(maxAge)) {
	dobDate.setFullYear(dobDate.getFullYear()+maxAge);
	if (currentTime >= dobDate.getTime()) {
		return "You must be under "+maxAge+" years old";
	}
}

return true;

